import React, { Component } from 'react';
import './App.css';
import Navbar from './Navbar.js';
import Homepage from './Homepage.js';
import Newclient from './Newclient.js';
import SearchClient from './SearchClient.js';
import NewService from './NewService.js'
import Work from './Work.js'
import { Switch, HashRouter, Route } from 'react-router-dom';




class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      client: []
    };
  }

  componentDidMount() {
    this.getAllData();
  }

  getAllData = () => {

    fetch('http://localhost:5000/')
      .then(res => res.json())
      .then(data => {
        console.log(data)
        this.setState({
          client: data
        });
      })
  }

  render() {
    console.log(this.client);
    return (


      <HashRouter basename="/Nita">
        <Navbar></Navbar>
        <Switch>
          <Route exact path="/" component={Homepage} />
          <Route exact path="/newclient" component={Newclient} />
          <Route exact path="/searchclient" component={SearchClient} clients={this.client} />
          <Route exact path="/newservice" component={NewService} />
          <Route exact path="/work" component={Work} />
        </Switch>
      </HashRouter>


    );
  }

}



export default App;
