import React from 'react';
import { Component } from 'react';


class Newclient extends Component {
    constructor(props) {
        super(props);
        this.state = {
            date: "",
            client: "",
            service: "",
            price: ""
        }
        this.updateDataField = this.updateDataField.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    updateDataField = (e) => {

        this.setState({
            [e.target.name]: e.target.value,

        });
    }
    handleSubmit = (e) => {

        fetch("/newclient",
            {
                method: 'POST',
                headers: new Headers({
                    'Content-Type': 'application/json'
                }),
                body: JSON.stringify(this.state),
            })
            .then(res => res.json())
            .then(
                res => this.setState({ "flash": res.flash }),
                err => this.setState({ "flash": err.flash })

            );


        e.preventDefault();

    }

    render() {
        return (

            <div className='new'>
                <p>Inserir Cliente Novo</p>
                <input type='date' name='date' value='data'></input>
                <input type='text' name='client' value='nome'></input>
                <input type='text' name='service' value='serviço'></input>
                <input type='text' name='price' value='preço'></input>
                <button type='submit'> gravar</button>
            </div>
        )
    }


}

export default Newclient;
